CREATE TABLE `drive_trackers` (`id` INT AUTO_INCREMENT, `name` VARCHAR(6) DEFAULT '' NOT NULL, `type` VARCHAR(15) DEFAULT NULL, `encrypt_id` VARCHAR(100) DEFAULT NULL, `encrypt_key` VARCHAR(100) DEFAULT NULL, `associations` text DEFAULT NULL, `legacy` BIGINT DEFAULT '0', `legacy_info` text DEFAULT NULL, `capacity` BIGINT, `free_space` BIGINT DEFAULT '0', `record_disabled` BIGINT, `year` year(4), PRIMARY KEY(`id`)) ENGINE = INNODB;
